import React, { useState } from 'react';
const addSport = async (sportName) => {
  const url = 'http://localhost:4000/addSport';
  const data = {
    sportsList: {  // Wrap sportName inside sportsList
      sportName
    }
  };
  console.log("DATA INSIDE OF addSPORT:", data);

  try {
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const result = await response.json();
    console.log(result);
    return result;
  } catch (error) {
    console.error('Error:', error);
  }
};



const SportsSearch = ({ addToWatchlist }) => {
  const [query, setQuery] = useState('');
  const [result, setResult] = useState(null);
  const [logo, setLogo] = useState(null);

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    const apiKey = 'AIzaSyA8te_WA4Hitv1gAO_GCcaTQkuWfZjZSoc';
    const searchEngineId = '91375e072088c4407';
    const searchUrl = `https://www.googleapis.com/customsearch/v1?key=${apiKey}&cx=${searchEngineId}&q=espn ${query}`;
    const imageSearchUrl = `https://www.googleapis.com/customsearch/v1?key=${apiKey}&cx=${searchEngineId}&q=${query} logo&searchType=image`;

    try {
      const searchResponse = await fetch(searchUrl);
      const searchData = await searchResponse.json();
      const firstResult = searchData.items && searchData.items[0];

      const imageResponse = await fetch(imageSearchUrl);
      const imageData = await imageResponse.json();
      const firstImage = imageData.items && imageData.items[0];

      if (firstResult) {
        setResult(firstResult.link);
      } else {
        setResult('No results found');
      }

      if (firstImage) {
        setLogo(firstImage.link);
      } else {
        setLogo(null);
      }
    } catch (error) {
      console.error('Error fetching data: ', error);
      setResult('An error occurred. Please try again later.');
      setLogo(null);
    }
  };

  const handleAddToWatchlist = () => {
    if (result && logo) {
      addToWatchlist({ teamName: query, link: result, logo });
      addSport([query]);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <form className="w-full max-w-md" onSubmit={handleSearch}>
        <div className="flex items-center border-b border-b-2 border-teal-500 py-2">
          <input
            type="text"
            className="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none"
            placeholder="Enter team name"
            value={query}
            onChange={handleInputChange}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                handleSearch(e);
              }
            }}
          />
          <button
            type="button"
            onClick={handleAddToWatchlist}
            className="flex-shrink-0 bg-blue-500 hover:bg-blue-700 text-white text-sm py-1 px-2 rounded"
            disabled={!result || !logo}
          >
            Add to Watchlist
          </button>
        </div>
      </form>
      {result && (
        <div className="mt-4">
          {logo && (
            <div className="mt-4">
              <a href={result} target="_blank" rel="noreferrer">
                <img src={logo} alt="Team Logo" className="w-32 h-32 object-contain" />
              </a>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SportsSearch;
