import React, { useState } from 'react';
import SportsSearch from './SportsSearch';

const SportsPage = () => {
  const fetchUserSports = async () => {
    const url = 'http://localhost:4000/getUserSports';

    try {
        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                // Include authentication tokens if needed, for example:
                // 'Authorization': 'Bearer yourTokenHere'
            }
        });

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Full response data:', data);  // This will log the full data object

        // If you want to see the movies array in a readable format:
        console.log('Sports array:', data.sports);  // Log the movies array directly
        //console.log('S array as string:', JSON.stringify(data.movies, null, 2));  // Pretty print the movies array

        return data.movies;  // Assuming the movies are correctly located under the 'movies' key
    } catch (error) {
        console.error('Error fetching sports:', error);
        return [];  // Return empty array on error
    }
  };

  const removeSport = async (movieTitle) => {
    const url = 'http://localhost:4000/removeMovie';
    const data = {
        movieTitle
    };

    try {
        const response = await fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        });

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const result = await response.json();
        console.log(result);
        return result;
    } catch (error) {
        console.error('Error:', error);
    }
};

  const [watchlist, setWatchlist] = useState([]);

  const addToWatchlist = (item) => {
    setWatchlist((prevWatchlist) => [...prevWatchlist, item]);

  };

  const deleteItem = (index) => {
    setWatchlist((prevWatchlist) => prevWatchlist.filter((_, i) => i !== index));
  };

  return (
    <div className="p-4">
      <SportsSearch addToWatchlist={addToWatchlist} />
      <div className="mt-8">
        <h2 className="text-2xl font-bold">Watchlist</h2>
        {watchlist.length === 0 ? (
          <p className="text-gray-500">Your watchlist is empty.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
            {watchlist.map((item, index) => (
              <div key={index} className="flex flex-col items-center p-4 bg-white shadow-md rounded-lg relative">
                <a href={item.link} target="_blank" rel="noreferrer">
                  <img src={item.logo} alt="Team Logo" className="w-32 h-32 object-contain" />
                </a>
                <button
                  onClick={() => deleteItem(index)}
                  className="absolute top-2 right-2 bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded-full"
                >
                  &times;
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SportsPage;
